CREATE TABLE event (id serial primary key, title varchar(50),date timestamp, address varchar(100), description varchar(255));
CREATE TABLE eventusers (id serial primary key, userid int, status varchar(50), owner boolean, eventid int);
INSERT INTO event (title, date, address, description) values ('Tasi morzsaparty', '2018-11-17', '6725 Szeged, Pásztor utca 2', 'Segítsetek megszabadulni a maradéktól!'), ('Bödön piac', '2018-11-10', '6724 Szeged, Kossuth Lajos sgt. 119', 'Újra kétnapos Bödön!! Ugye tudod, mit jelent ez?!'), ('Lámpás séta Szegeden', '2018-11-10', '6722 Szeged, Kálvária sugárút 23', 'A program díja: 500 Ft/fő, amellyel egy téli szüneti programon is részt lehet venni december 27-én vagy 28-án. '), ('16. Olasz Filmfesztivál', '2018-11-09', '6720 Szeged, Vaszy Viktor tér 3', 'Válogatás a legújabb olasz filmekből eredeti nyelven, magyar felirattal'), ('Munjin Andrea kiállításmegnyitó', '2018-11-10', '6720 Szeged, Deák Ferenc utca 18', 'Föllép a Lucy Dreamin akusztikus duóverziója Lucy Chillin néven, valamint Lanczkor Gábor Anarchitecture, Médeia fiai új szólóprojektje is bemutatkozik Los Reyes Católicos néven.'), ('Creativ_it meetup - Enterprise Gamification', '2018-11-07', '6722 Szeged, Kálvária sgt 23', 'ingyenes meetupot szervez, ahol a gamification vállalati megoldásairól hallgathatsz meg egy előadást.'), ('Legendás Lemezek - Szegedi Lemezbörze', '2018-11-24', '6725 Szeged, Rákóczy utca 1', 'Biggest record fair in south of Hungary. The last one this year.'), ('Van Gogh – Búzamezők és borús égbolt között', '2018-11-24', '6720 Szeged, Vaszy Viktor tér 3', 'Ebben a művészeti ismeretterjesztő filmben Vincent Van Gogh munkássága egy egészen új nézőpontból kerül megközelítésre'), ('Belvárosi Magyar Ede séta', '2018-11-18', '6720 Szeged, Széchenyi tér 12', 'A sétán a Reök-palotát megálmodó zseniális építész legszebb szecessziós épületeit ismerhetjük meg – kívül-belül!'), ('Diótörő', '2018-12-15', 'Szegedi Nemzeti Színház', 'A Diótörő balett ötlete a cári színház egykori igazgatójától származott. E. T. A. Hoffmann: A diótörő és az egérkirály című meséje alapján olyan mese-balettet akart színpadra vinni, ami minden addigit felülmúl, mind hangzásban, mind pedig látványban.');
